export default class A1Z26{constructor(str){this.textToProcess=str}encrypt(){return this.textToProcess.toUpperCase().split("").filter(n=>/\w/.test(n)).map(n=>n.charCodeAt(0)-64).join(" ")}decrypt(){return this.textToProcess.split(" ").filter(n=>/\d/.test(n)).map(n=>String.fromCharCode(Number(n)+64)).join("")}};